parameters.body/messageBody:
{
    "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
    "type": "AdaptiveCard",
    "version": "1.3",
    "body": [
       {
            "type": "Container",
"style": "accent",
            "items": [
        {
            "type": "TextBlock",
            "text": "Daily @{variables('dow')} - @{outputs('prefix_first_user')}@{outputs('form_data').name}",
            "weight": "Bolder",
            "size": "medium"
        },
        {
            "type": "TextBlock",
            "text": "@{outputs('Translations').achievedBeforeTitle}:",
            "weight": "Bolder",
            "wrap": true,
            "spacing": "Medium"
        },
        {
            "type": "TextBlock",
            "text": "@{outputs('form_data').before}",
            "wrap": true,
            "spacing": "None"
        },
        {
            "type": "TextBlock",
            "text": "@{outputs('Translations').goalsTodayTitle}:",
            "weight": "Bolder",
            "wrap": true,
            "spacing": "Medium"
        },
        {
            "type": "TextBlock",
            "text": "@{outputs('form_data').today}",
            "wrap": true,
            "spacing": "None"
        },
        {
            "type": "TextBlock",
            "text": "@{outputs('Translations').blockersTitle}:",
            "weight": "Bolder",
            "wrap": true,
            "spacing": "Medium"
        },
        {
            "type": "TextBlock",
            "text": "@{outputs('form_data').blocking}",
            "wrap": true,
            "spacing": "None"
        }
      ]
   }
 ]
}
